from . import AWSObject, AWSProperty
from .validators import positive_integer, boolean


class MetricDimension(AWSProperty):
    props = {
        'Name': (str, True),
        'Value': (str, True),
    }


class Alarm(AWSObject):
    resource_type = "AWS::CloudWatch::Alarm"

    props = {
        'ActionsEnabled': (boolean, False),
        'AlarmActions': ([str], False),
        'AlarmDescription': (str, False),
        'AlarmName': (str, False),
        'ComparisonOperator': (str, True),
        'Dimensions': ([MetricDimension], False),
        'EvaluationPeriods': (positive_integer, True),
        'InsufficientDataActions': ([str], False),
        'MetricName': (str, True),
        'Namespace': (str, True),
        'OKActions': ([str], False),
        'Period': (positive_integer, True),
        'Statistic': (str, True),
        'Threshold': (str, True),
        'Unit': (str, False),
    }
